class Creature {	protected int posX, posY;	protected String name;	public Creature(int posX, int posY, String name) {		this.posX = posX;		this.posY = posY;		this.name = name;	}	public int getX() {		return posX;	}	public int getY() {		return posY;	}	public String getName() {		return name;	}	public void changePosX(int posX) {		this.posX = posX;	}	public void changePosY(int posY) {		this.posY = posY;	}	public void changePos(int posX, int posY) {		this.posX = posX;		this.posY = posY;	}	public void moveTo(int posX, int posY) {		changePosX(posX);		changePosY(posY);	}	/*	public static void main(String[] args) {		HuluBrother c = new HuluBrother(2, 3, "红娃", 1);		System.out.println(c.getName());	}	*/}class HuluBrother extends Creature {	private int rank;	public HuluBrother(int posX, int posY, String name, int rank) {		super(posX, posY, name);		this.rank = rank;	}}class GrandPa extends Creature {	public GrandPa(int posX, int posY, String name) {		super(posX, posY, name);	}}class SnakerMonster extends Creature {	public SnakerMonster(int posX, int posY, String name) {		super(posX, posY, name);	}}class ScorpionMonster extends Creature {	public ScorpionMonster(int posX, int posY, String name) {		super(posX, posY, name);	}}class LittleMonster extends Creature {	public LittleMonster(int posX, int posY, String name) {		super(posX, posY, name);	}}